    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        String query;
        String log = jTextField1.getText();
        String password = jPasswordField1.getText();
        int  fct=jComboBox1.getSelectedIndex();
        if(fct ==0){
            query="select nom, password from admin";
            try{
                st=cnx.obtenircon().createStatement();
                ResultSet rs=st.executeQuery(query);
                while(rs.next()){
                    String id2=rs.getString("nom");
                    String pass2=rs.getString("password");
                    System.out.println(id2);
                    System.out.println(pass2);
                    if((log.equals(id2))&&(pass2.equals(password))){
                        acceuil acc=new acceuil();
                        acc.setVisible(true);
                        this.setVisible(false);
                    }else{
                        JOptionPane.showMessageDialog(rootPane,"Verifier");
                    }}
                
            }catch(SQLException e){
                JOptionPane.showMessageDialog(null,"erreur d'affichage");
                
            }}else if (fct==1) {
                
            try{
                st=cnx.obtenircon().createStatement();
                ResultSet rs=st.executeQuery("select nom, pass from ouvriers where fonction ='Gérant'");
                while(rs.next()){
                    String id2=rs.getString("nom");
                    String pass2=rs.getString("pass");
                    System.out.println(id2);
                    System.out.println(pass2);
                    if((log.equals(id2))&&(pass2.equals(password))){
                        acceuil_gerant accg=new acceuil_gerant();
                        accg.setVisible(true);
                        this.setVisible(false);
                    }else{
                        JOptionPane.showMessageDialog(rootPane,"Verifier");
                    }}
                
            }catch(SQLException e){
                JOptionPane.showMessageDialog(null,"erreur d'affichage");
                
            }}
    }//GEN-LAST:event_jButton1ActionPerformed

