    private void jComboBox1ItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_jComboBox1ItemStateChanged
        try{
                 st=cnx.obtenircon().createStatement();
                ResultSet rs=st.executeQuery("select nom from ouvriers where fonction ='Gérant'"); 
                while(rs.next()){
                 if(jComboBox1.getSelectedIndex()==1){
                     jTextField1.setText(rs.getString("nom"));jTextField1.setEditable(false);
                 }else if (jComboBox1.getSelectedIndex()==0){jTextField1.setText("");jTextField1.setEditable(true);}
                    }
                }catch(SQLException e){JOptionPane.showMessageDialog(rootPane, e);}
    }//GEN-LAST:event_jComboBox1ItemStateChanged

